export async function getStaticProps(): Promise<any> {
  try {
    const { data } = await axios.get(`${process.env.NEXT_PUBLIC_API_URL}/home`)
    const { menu = [], facebook = '', instagram = '', twitter = '' } = data

    return {
      props: { config: { menu, facebook, instagram, twitter } },
    }
  } catch (err) {
    return {
      props: {
        config: {
          menu: [],
          facebook: '',
          instagram: '',
          twitter: '',
        },
      },
    }
  }
}

